import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class ProveedoresService {
  proveedor = {
    codigo: '',
    nombre: '',
    direccion: '',
    telefono: '',
    correo: '',
  };
  private url = 'https://192.168.137.1:3001/proveedores';
  constructor(private http: HttpClient) {}

  guardar(
    codigo: string,
    nombre: string,
    direccion: string,
    telefono: string,
    correo: string
  ) {
    const proveedor = {
      codigo: codigo,
      nombre: nombre,
      direccion: direccion,
      telefono: telefono,
      correo: correo,
    };
    return this.http.post(this.url, proveedor);
  }

  consultarTodo() {
    return this.http.get<any>(this.url);
  }

  consultar(codigo: string) {
    return this.http.get<any>(this.url + '/datos/' + codigo);
  }

  eliminar(codigo: string) {
    return this.http.delete<any>(this.url + '/eliminar/' + codigo);
  }

  modificar(proveedor: object) {
    return this.http.put(this.url, proveedor);
  }
}
